package im.zhaojun.zfile.module.config.model.request;import io.swagger.annotations.ApiModel;import io.swagger.annotations.ApiModelProperty;import lombok.Data;import javax.validation.constraints.NotBlank;/** * 用户修改密码请求参数类 * * @author zhaojun */@Data@ApiModel(description = "用户修改密码请求参数类")public class UpdateUserNameAndPasswordRequest {    @ApiModelProperty(value = "用户名", required = true, example = "admin")    @NotBlank(message = "用户名不能为空")    private String username;    @ApiModelProperty(value = "密码", required = true, example = "123456")    @NotBlank(message = "密码不能为空")    private String password;}